// TestSealPrivatePayload tests seal and encrypt private tx's payload.
func TestSealPrivatePayload(t *testing.T) {
	fakeIpfsAdapter := database.NewFakeIpfsAdapter()
	ipfsDb := database.NewIpfsDbWithAdapter(fakeIpfsAdapter)
	callSealPrivatePayload(t, ipfsDb)
}

// TestGetPrivateKeyForAccount tests getting private key for given account.
func TestGetPrivateKeyForAccount(t *testing.T) {
	account1 := "e94b7b6c5a0e526a4d97f9768ad6097bde25c62a"
	prv1 := GetPrivateKeyForAccount(account1)
	if prv1 == nil {
		t.Fatalf("The private key for %s should be retrieved.", account1)
	}
}

// TestSealPrivatePayloadWithFaultIPFS tests the SealPrivatePayload function with fault IPFS server.
func TestSealPrivatePayloadWithFaultIPFS(t *testing.T) {
	payload := []byte("This is a payload plaintext.")
	txNonce := uint64(10000)

	parties := []string{"0x04ca634cae0d49acb401d8a4c6b6fe8c55b70d115bf400769cc1400f3258cd31387574077f301b421bc84df7266c44e9e6d569fc56be00812904767bf5ccd1fc7f",
		"0x04ca634cae0d49acb401d8a4c6b6fe8c55b70d115bf400769cc1400f3258cd31387574077f301b421bc84df7266c44e9e6d569fc56be00812904767bf5ccd1fc7f"}

	adapter := FaultIpfsAdapter{}
	ipfsDb := database.NewIpfsDbWithAdapter(&adapter)
	_, err := SealPrivatePayload(payload, txNonce, parties, ipfsDb)
	if err == nil {
		t.Fatal("It should return an error when IPFS is in fault.")
	}
}

func callSealPrivatePayload(t *testing.T, remoteDB database.RemoteDatabase) {
	payload := []byte("This is a payload plaintext.")
	txNonce := uint64(10000)

	parties := []string{"0x04ca634cae0d49acb401d8a4c6b6fe8c55b70d115bf400769cc1400f3258cd31387514077f301b421bc84df7266c44e9e6d519fc56be00812904767bf5ccd1fc7f",
		"0x04ca634cae0d49acb401d8a4c6b6fe8c55b70d115bf400769cc1400f3258cd3131574077f301b421bc84df7266c44e9e6d569fc56be00812901767bf5ccd1fc7f"}

	replacement, err := SealPrivatePayload(payload, txNonce, parties, remoteDB)
	if err != nil {
		t.Fatal("It should return expected IPFS address without any error.")
	}
	if len(replacement.Participants) == 0 {
		t.Fatal("It should return non-empty participants.")
	}

	content, err := remoteDB.Get(replacement.TxPayload)
	if err != nil {
		t.Fatal("It should return expected content from IPFS.")
	}

	sealedPayload := SealedPrivatePayload{}
	err = rlp.DecodeBytes(content, &sealedPayload)
	if err != nil {
		t.Fatal("The content retrieved from IPFS should be decoded into a SealedPrivatePayload object.")
	}
	if len(sealedPayload.Participants) == 0 {
		t.Fatal("The retrieved sealed payload should hava more than zero participants.")
	}
	if len(sealedPayload.SymmetricKeys) != len(sealedPayload.Participants) {
		t.Fatal("The number of participants should be equal to encrypted keys.")
	}
	if len(sealedPayload.Payload) == 0 {
		t.Fatal("The payload should not be empty.")
	}
}

